import React from 'react';
import { post } from 'axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import { withStyles } from '@material-ui/core/styles'

const styles = theme => ({
    hidden: {
        display: 'none'
    }
});

class CustomerPost extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        }
    }

    handleFormSubmit = (e) => {
        e.preventDefault();
        this.postCustomers()
            .then((res) => {
                console.log(res);
                this.props.stateRefresh();
            });
        this.setState({
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: ''
        });
    }

    handleFileChange = (e) => {
        this.setState({
           file: e.target.files[0],
           fileName: e.target.value
        });
    }

    handleValueChange = (e) => {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
    }

    postCustomers = () => {
        const url = '/api/customers';
        const formData = new FormData;
        formData.append('image', this.state.file);
        formData.append('name', this.state.userName);
        formData.append('birthday', this.state.birthday);
        formData.append('gender', this.state.gender);
        formData.append('job', this.state.job);
        const config = {
            headers: {
                'content-type': 'multipart/form-date'
            }
        }
        return post(url, formData, config);
    }

    handleClickOpen = () => {
        this.setState({
            open: true
        })
    }

    handleClose = () => {
        this.setState({
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        });
    }

    render() {
        const { classes } = this.props;
        return (
            <div>
                <Button variant='contained' color='primary' onClick={this.handleClickOpen}>
                    고객 등록하기
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose}>
                    <DialogTitle>고객 등록</DialogTitle>
                    <DialogContent>
                        <input className={classes.hidden} accept='image/' id='raised-button-file' type='file' file={this.state.file} value={this.state.fileName} onChange={this.handleFileChange}/><br/>
                        <label htmlFor='button'>
                            <Button variant='contained' color='primary' component='span' name='file'>
                                {this.state.fileName === '' ? '프로필 이미지 선택':this.state.fileName}
                            </Button>
                        </label><br/>
                        <TextField type='text' name='userName' value={this.state.userName} onChange={this.handleValueChange}/><br/>
                        <TextField type='text' name='birthday' value={this.state.birthday} onChange={this.handleValueChange}/><br/>
                        <TextField type='text' name='gender' value={this.state.gender} onChange={this.handleValueChange}/><br/>
                        <TextField type='text' name='job' value={this.state.job} onChange={this.handleValueChange}/><br/>
                    </DialogContent>
                    <DialgoActions>
                        <Button variant='contained' color='primary' onClick={this.handleFormSubmit}>추가</Button>
                        <Button variant='outlined' color='primary' onClick={this.handleClose}>닫기</Button>
                    </DialgoActions>
                </Dialog>
            </div>
        )
    }
}

export default withStyles(styles)(CustomerPost);
